  export default {
    name:'details',

    data: function(){
      return {
        city : '' ,
        title:'',
        cityName:'',
        imageCity:'' ,
        imageRole:'' ,
        role:'',
        details: [
          {img:'https://i.ibb.co/5xRGwW5/homeless.png' , name: 'Homeless',role:'allhelpless', id: 'HOMELESS'},
          {img:'https://i.ibb.co/948KGdK/shutterstock-2630522811-3-390x285.jpg',name: 'Donors',role:'donors', id:'DONOR'},
